// src/escrow.js

import * as anchor from "@coral-xyz/anchor";
import { PublicKey, SystemProgram } from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";
import { getProvider } from "./solana";

// Grab the programId from the IDL's metadata.address field
const PROGRAM_ID = new PublicKey(idl.metadata.address);

/**
 * Initialize a new escrow
 * @param {number} wagerLamports amount in lamports
 * @returns {Promise<PublicKey>} PDA of escrow
 */
export async function initializeEscrow(wagerLamports) {
  const provider = getProvider();
  const program = new anchor.Program(idl, PROGRAM_ID, provider);

  // nonce for uniqueness
  const nonce = Math.floor(Math.random() * 1000000);

  // derive PDA
  const [escrowPDA] = await PublicKey.findProgramAddress([Buffer.from("escrow"), provider.wallet.publicKey.toBuffer(), new anchor.BN(nonce).toArrayLike(Buffer, "le", 8)], program.programId);

  // send the tx
  await program.methods.initialize(new anchor.BN(wagerLamports), new anchor.BN(nonce)).accounts({
    escrow: escrowPDA,
    player1: provider.wallet.publicKey,
    authority: provider.wallet.publicKey,
    systemProgram: SystemProgram.programId
  }).rpc({
    // you can tweak these if you need faster confirmations
    skipPreflight: true,
    preflightCommitment: "processed"
  });
  return escrowPDA;
}

/**
 * Player 2 joins and deposits in one go
 * @param {PublicKey} escrowPDA
 */
export async function joinAndDeposit(escrowPDA) {
  const provider = getProvider();
  const program = new anchor.Program(idl, PROGRAM_ID, provider);

  // fetch the escrow account to get its escrowAccount field
  const escrowAccount = await program.account.escrow.fetch(escrowPDA);
  await program.methods.joinAndDeposit().accounts({
    escrow: escrowPDA,
    player2: provider.wallet.publicKey,
    escrowAccount: escrowAccount.escrowAccount,
    systemProgram: SystemProgram.programId
  }).rpc({
    skipPreflight: true,
    preflightCommitment: "processed"
  });
}

/**
 * Just deposit (for player1 second deposit or so)
 * @param {PublicKey} escrowPDA
 */
export async function depositToEscrow(escrowPDA) {
  const provider = getProvider();
  const program = new anchor.Program(idl, PROGRAM_ID, provider);
  const escrowAccount = await program.account.escrow.fetch(escrowPDA);
  await program.methods.deposit().accounts({
    escrow: escrowPDA,
    payer: provider.wallet.publicKey,
    escrowAccount: escrowAccount.escrowAccount,
    systemProgram: SystemProgram.programId
  }).rpc({
    skipPreflight: true,
    preflightCommitment: "processed"
  });
}

/**
 * Cancel an escrow and refund both sides
 * @param {PublicKey} escrowPDA
 * @param {PublicKey} player1Pubkey
 * @param {PublicKey} player2Pubkey
 */
export async function cancelEscrow(escrowPDA, player1Pubkey, player2Pubkey) {
  const provider = getProvider();
  const program = new anchor.Program(idl, PROGRAM_ID, provider);
  const escrowAccount = await program.account.escrow.fetch(escrowPDA);
  await program.methods.cancel().accounts({
    escrow: escrowPDA,
    player1: player1Pubkey,
    player2: player2Pubkey,
    escrowAccount: escrowAccount.escrowAccount,
    authority: provider.wallet.publicKey
  }).rpc({
    skipPreflight: true,
    preflightCommitment: "processed"
  });
}

/**
 * Resolve escrow by sending all funds to the winner
 * @param {PublicKey} escrowPDA
 * @param {PublicKey} winnerPubkey
 */
export async function resolveEscrow(escrowPDA, winnerPubkey) {
  const provider = getProvider();
  const program = new anchor.Program(idl, PROGRAM_ID, provider);
  const escrowAccount = await program.account.escrow.fetch(escrowPDA);
  await program.methods.resolve(new PublicKey(winnerPubkey)).accounts({
    escrow: escrowPDA,
    winner: new PublicKey(winnerPubkey),
    house: provider.wallet.publicKey,
    escrowAccount: escrowAccount.escrowAccount,
    authority: provider.wallet.publicKey
  }).rpc({
    skipPreflight: true,
    preflightCommitment: "processed"
  });
}

