// src/solana.js
import * as anchor from "@coral-xyz/anchor";
import { Connection, clusterApiUrl, PublicKey } from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";

// Program ID from the IDL (metadata.address)
export const PROGRAM_ID = new PublicKey(idl.metadata.address);

/**
 * Call once after wallet connects.
 */
export function setProvider(wallet) {
  if (!wallet || !wallet.publicKey) {
    throw new Error("Wallet not connected");
  }
  const network = process.env.REACT_APP_SOLANA_NETWORK || "mainnet-beta";
  const rpcEndpoint =
    process.env.REACT_APP_SOLANA_RPC || clusterApiUrl(network);

  const connection = new Connection(rpcEndpoint, "confirmed");
  const provider = new anchor.AnchorProvider(connection, wallet, {
    preflightCommitment: "confirmed",
    commitment: "confirmed",
  });
  anchor.setProvider(provider);
}

/**
 * Returns the global AnchorProvider.
 */
export function getProvider() {
  return anchor.getProvider();
}

/**
 * Returns a typed Program client.
 */
export function getProgram() {
  const provider = getProvider();
  return new anchor.Program(idl, PROGRAM_ID, provider);
}
