// src/solana.js
import * as anchor from "@coral-xyz/anchor";
import { PublicKey } from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";

// Load PROGRAM_ID from your IDL (address property or metadata.address)
export const PROGRAM_ID = new PublicKey(idl.address || idl.metadata.address);

// Export nothing else here! All provider logic happens per-transaction with user wallet

// This file only exports the PROGRAM_ID and can be imported in escrow.js like:
// import { PROGRAM_ID } from "./solana";
