// frontend/src/escrow.js

import * as anchor from "@coral-xyz/anchor";
import {
  PublicKey,
  SystemProgram,
  TransactionInstruction,
} from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";
import { PROGRAM_ID, getProvider } from "./solana";

/** Lazily grab your Anchor Program; must have called setProvider(wallet) first */
function getProgram() {
  const provider = getProvider();
  if (!provider) {
    throw new Error(
      "Provider not set: call setProvider(wallet) in App.jsx before using escrow.js"
    );
  }
  return new anchor.Program(idl, PROGRAM_ID, provider);
}

/**
 * 1) Initialize + deposit (via Anchor so on‑chain state flips p1_deposited=true)
 */
export async function buildInitializeAndDepositIxns(
  lamports,
  initializerPubkey,
  nonce
) {
  const program = getProgram();

  // pack nonce into 8‑byte LE buffer
  const nonceBuf = Buffer.from(new anchor.BN(nonce).toArray("le", 8));

  // derive PDA exactly as in Rust
  const [escrowPda] = await PublicKey.findProgramAddress(
    [Buffer.from("escrow"), initializerPubkey.toBuffer(), nonceBuf],
    PROGRAM_ID
  );

  // (a) initialize: creates the account & sets up state
  const ixInit = await program.methods
    .initialize(new anchor.BN(lamports), new anchor.BN(nonce))
    .accounts({
      escrow:         escrowPda,
      player1:        initializerPubkey,
      authority:      initializerPubkey,
      system_program: SystemProgram.programId,  // snake_case
    })
    .instruction();

  // (b) deposit: does the SOL transfer AND flips p1_deposited = true on‑chain
  const ixDeposit = await program.methods
    .deposit()
    .accounts({
      escrow:          escrowPda,
      payer:           initializerPubkey,
      escrow_account:  escrowPda,              // snake_case
      system_program:  SystemProgram.programId,// snake_case
    })
    .instruction();

  return { ixInit, ixDeposit, escrowPda };
}

/**
 * 2) Join & deposit in one go
 */
export async function buildJoinAndDepositIxn(escrowPda, wager) {
  const program = getProgram();
  return program.methods
    .joinAndDeposit(new anchor.BN(wager))
    .accounts({
      escrow:          escrowPda,
      player2:         program.provider.wallet.publicKey,
      escrow_account:  escrowPda,               // snake_case
      system_program:  SystemProgram.programId, // snake_case
    })
    .instruction();
}

/**
 * 3) Cancel an escrow (manual TransactionInstruction + debug)
 */
export function buildCancelIxn(
  escrowPda,
  initializerPubkey,
  accepterPubkey
) {
  console.log(
    "IDL cancel accounts:",
    idl.instructions.find((ix) => ix.name === "cancel").accounts
  );

  // build the 8‑byte discriminator
  const cancelIx = idl.instructions.find((ix) => ix.name === "cancel");
  const data = Buffer.from(cancelIx.discriminator);

  // list each AccountMeta exactly as Rust expects
  const keys = [
    { pubkey: escrowPda,         isSigner: false, isWritable: true  },
    { pubkey: initializerPubkey, isSigner: false, isWritable: true  },
    { pubkey: accepterPubkey,    isSigner: false, isWritable: true  },
    { pubkey: escrowPda,         isSigner: false, isWritable: true  },
    { pubkey: initializerPubkey, isSigner: true,  isWritable: true  },
  ];

  console.log("Built Cancel ix metas:", keys);

  return new TransactionInstruction({
    programId: PROGRAM_ID,
    keys,
    data,
  });
}

/**
 * 4) Resolve/payout
 */
export async function buildResolveIxn(escrowPda, winnerPubkey) {
  const program = getProgram();
  const winnerKey =
    typeof winnerPubkey === "string"
      ? new PublicKey(winnerPubkey)
      : winnerPubkey;

  return program.methods
    .resolve(winnerKey)
    .accounts({
      escrow:          escrowPda,
      winner:          winnerKey,
      house:           program.provider.wallet.publicKey,
      escrow_account:  escrowPda,               // snake_case
    })
    .instruction();
}
