import * as anchor from "@coral-xyz/anchor";
import { Connection, PublicKey } from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";

const RPC_URL = process.env.REACT_APP_SOLANA_RPC;
const connection = new Connection(RPC_URL, "confirmed");

/**
 * Call this once from your App.jsx to wire up the wallet
 * into anchor.setProvider(…).
 */
export function setProvider(wallet) {
  const provider = new anchor.AnchorProvider(
    connection,
    wallet,
    {
      preflightCommitment: "confirmed",
      commitment: "confirmed",
    }
  );
  anchor.setProvider(provider);
}

/** Program ID from your IDL */
export const PROGRAM_ID = new PublicKey(idl.metadata.address);

/** Expose the raw connection if you need it elsewhere */
export { connection };
