// frontend/src/escrow.js

import * as anchor from "@coral-xyz/anchor";
import { PublicKey, SystemProgram } from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";
import { PROGRAM_ID, getProvider } from "./solana";

/** Lazily grab your Anchor Program; must have called setProvider(wallet) first */
function getProgram() {
  const provider = getProvider();
  if (!provider) {
    throw new Error("Provider not set: call setProvider(wallet) in App.jsx before using escrow.js");
  }
  return new anchor.Program(idl, PROGRAM_ID, provider);
}

/**
 * 1) Initialize + deposit: derive PDA, call initialize(wager, nonce), then a transfer
 * @param {number} lamports             Amount of SOL to stake (in lamports)
 * @param {PublicKey} initializerPubkey Your wallet’s PublicKey
 * @param {number} nonce                A fresh random nonce
 */
export async function buildInitializeAndDepositIxns(lamports, initializerPubkey, nonce) {
  const program = getProgram();

  // Pack nonce into an 8‑byte LE Buffer to match &nonce.to_le_bytes()
  const nonceBuf = Buffer.from(new anchor.BN(nonce).toArray("le", 8));

  // Derive the PDA exactly as in Rust: ["escrow", player1, &nonce.to_le_bytes()]
  const [escrowPda] = await PublicKey.findProgramAddress([Buffer.from("escrow"), initializerPubkey.toBuffer(), nonceBuf], PROGRAM_ID);

  // 1️⃣ Initialize on‑chain (wager, nonce)
  const ixInit = await program.methods.initialize(new anchor.BN(lamports), new anchor.BN(nonce)).accounts({
    escrow: escrowPda,
    // #[account(init...)]
    player1: initializerPubkey,
    // signer
    authority: initializerPubkey,
    // payer
    system_program: SystemProgram.programId // <— snake_case
  }).instruction();

  // 2️⃣ Deposit SOL into the PDA vault
  const ixDeposit = SystemProgram.transfer({
    fromPubkey: initializerPubkey,
    toPubkey: escrowPda,
    lamports
  });
  return {
    ixInit,
    ixDeposit,
    escrowPda
  };
}

/**
 * 2) Join & deposit in one go (RPC: join_and_deposit)
 * @param {PublicKey} escrowPda The PDA you derived earlier
 */
export async function buildJoinAndDepositIxn(escrowPda) {
  const program = getProgram();
  return program.methods.joinAndDeposit().accounts({
    escrow: escrowPda,
    // Account<Escrow>
    player2: program.provider.wallet.publicKey,
    // signer
    escrow_account: escrowPda,
    // raw PDA for lamports
    system_program: SystemProgram.programId // snake_case
  }).instruction();
}

/**
 * 3) Cancel an escrow (RPC: cancel)
 * @param {PublicKey} escrowPda
 * @param {PublicKey} initializerPubkey
 * @param {PublicKey} accepterPubkey
 */
export async function buildCancelIxn(escrowPda, initializerPubkey, accepterPubkey) {
  const program = getProgram();
  return program.methods.cancel().accounts({
    escrow: escrowPda,
    // Account<Escrow>
    player1: initializerPubkey,
    // raw AccountInfo
    player2: accepterPubkey,
    // raw AccountInfo
    escrow_account: escrowPda,
    // raw PDA for lamports
    authority: initializerPubkey // signer (must match escrow.authority)
  }).instruction();
}

/**
 * 4) Resolve/payout (RPC: resolve)
 * @param {PublicKey} escrowPda
 * @param {PublicKey | string} winnerPubkey
 */
export async function buildResolveIxn(escrowPda, winnerPubkey) {
  const program = getProgram();
  const winnerKey = typeof winnerPubkey === "string" ? new PublicKey(winnerPubkey) : winnerPubkey;
  return program.methods.resolve(winnerKey).accounts({
    escrow: escrowPda,
    // Account<Escrow>
    winner: winnerKey,
    // raw AccountInfo
    house: program.provider.wallet.publicKey,
    // signer
    escrow_account: escrowPda // raw PDA for lamports
  }).instruction();
}

