import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import sentences from "./sentences";
import { useConnection, useWallet } from "@solana/wallet-adapter-react";
import { WalletMultiButton } from "@solana/wallet-adapter-react-ui";
import {
  LAMPORTS_PER_SOL,
  PublicKey,
  Transaction,
} from "@solana/web3.js";
import socket from "./socket";
import * as anchor from "@coral-xyz/anchor";
import idl from "./idl/typeracer_escrow.json";
import { setProvider } from "./solana";
import {
  buildInitializeAndDepositIxns,
  buildJoinAndDepositIxn,
  buildCancelIxn,
  buildResolveIxn,
} from "./escrow";

export default function App() {
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // Hook up wallet + anchor provider
  const { connection } = useConnection();
  const wallet = useWallet();
  const { publicKey, connected } = wallet;
  const walletAddress = publicKey?.toBase58() || "";
  const walletAddressRef = useRef(walletAddress);
  useEffect(() => { walletAddressRef.current = walletAddress; }, [walletAddress]);
  useEffect(() => {
    if (connected && connection) {
      const prov = new anchor.AnchorProvider(connection, wallet, {
        preflightCommitment: "confirmed",
        commitment: "confirmed",
      });
      anchor.setProvider(prov);
      setProvider(wallet);
    }
  }, [connected, connection, wallet]);
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // Sound & Mute state
  const [muted, setMuted] = useState(false);
  const engineAudio   = useRef(new Audio("/engine.mp3"));
  const countdownAudio= useRef(new Audio("/countdown.mp3"));
  const errorAudio    = useRef(new Audio("/error.mp3"));
  useEffect(() => {
    engineAudio.current.loop = true;
  }, []);
  function playEngine() {
    if (!muted) engineAudio.current.play().catch(() => {});
  }
  function stopEngine() {
    engineAudio.current.pause();
    engineAudio.current.currentTime = 0;
  }
  function playCountdown() {
    if (!muted) countdownAudio.current.play().catch(() => {});
  }
  function playError() {
    if (!muted) errorAudio.current.play().catch(() => {});
  }
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // Wallet balance
  const [walletBalance, setWalletBalance] = useState(0);
  useEffect(() => {
    if (connection && publicKey) {
      connection.getBalance(publicKey).then((lamports) => {
        setWalletBalance(lamports / LAMPORTS_PER_SOL);
      });
    }
  }, [connection, publicKey]);
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // Game & matchmaking state
  const [wager, setWager] = useState(1);
  const [openMatches, setOpenMatches] = useState([]);
  const [currentMatch, setCurrentMatch] = useState(null);
  const [matchStatus, setMatchStatus] = useState("waiting");
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // Race / typing state
  const [sentence, setSentence] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [playerProgress, setPlayerProgress] = useState(0);
  const [opponentProgress, setOpponentProgress] = useState(0);
  const [countdown, setCountdown] = useState(3);
  const [raceResult, setRaceResult] = useState(null);
  const [isInputDisabled, setIsInputDisabled] = useState(false);
  const [isAccepting, setIsAccepting] = useState(false);
  const [opponentWallet, setOpponentWallet] = useState(null);

  // error‚Äêhighlight if mistyped
  const [hasError, setHasError] = useState(false);

  const inputRef = useRef(null);
  const API = process.env.REACT_APP_API_URL;

  // anti‚Äëcheat
  const [lastProgTime, setLastProgTime] = useState(Date.now());
  const [lastProg, setLastProg] = useState(0);
  const MAX_CPS = 20;
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // Helpers
  const chooseSentence = (id) =>
    sentences[
      Array.from(id).reduce((a, c) => a + c.charCodeAt(0), 0) %
        sentences.length
    ];

  async function fetchOpenMatches() {
    try {
      const res = await fetch(`${API}/wagers`);
      setOpenMatches(await res.json());
    } catch {}
  }

  function resetGame() {
    stopEngine();
    setCurrentMatch(null);
    setMatchStatus("waiting");
    setSentence("");
    setInputValue("");
    setPlayerProgress(0);
    setOpponentProgress(0);
    setCountdown(3);
    setRaceResult(null);
    setIsInputDisabled(false);
    setOpponentWallet(null);
    setLastProg(0);
    setLastProgTime(Date.now());
    setHasError(false);
    fetchOpenMatches();
  }

  // ‚îÄ Socket.io setup ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  useEffect(() => {
    socket.connect();

    socket.on("openMatches", setOpenMatches);
    socket.on("newMatch", (m) => setOpenMatches((os) => [...os, m]));
    socket.on("removeMatch", (id) =>
      setOpenMatches((os) => os.filter((m) => m.id !== id))
    );
    socket.on("startMatch", ({ id, players }) => {
      // begin countdown
      if (!currentMatch || currentMatch.id !== id) {
        setCurrentMatch({
          id,
          amount: currentMatch?.amount || 0,
          creator: players[0],
          accepter: players[1],
          nonce: currentMatch?.nonce,
        });
      }
      setMatchStatus("ready");
      setSentence(chooseSentence(id));
      setCountdown(3);
      setOpponentWallet(players.find((w) => w !== walletAddressRef.current));
      playCountdown();
    });
    socket.on("opponentProgress", ({ wallet: w, progress }) => {
      if (w !== walletAddressRef.current) setOpponentProgress(progress);
    });
    socket.on("raceEnd", ({ winner, progressMap }) => {
      setMatchStatus("finished");
      setIsInputDisabled(true);
      setRaceResult({ result: winner === walletAddressRef.current ? "win" : "lose" });
      if (progressMap) {
        setPlayerProgress(progressMap[walletAddressRef.current] ?? 0);
        setOpponentProgress(progressMap[opponentWallet] ?? 0);
      }
      stopEngine();
    });
    socket.on("chat", setChatMessages);
    socket.on("opponentLeft", () => resetGame());

    fetchOpenMatches();

    return () => {
      socket.removeAllListeners();
      socket.disconnect();
    };
  }, []); // once

  // Countdown ‚Üí ‚Äúracing‚Äù
  useEffect(() => {
    if (matchStatus === "ready" && countdown > 0) {
      const t = setTimeout(() => setCountdown((c) => c - 1), 1000);
      return () => clearTimeout(t);
    } else if (matchStatus === "ready" && countdown === 0) {
      setMatchStatus("racing");
      setIsInputDisabled(false);
      inputRef.current?.focus();
      playEngine();
    }
  }, [countdown, matchStatus]);
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // Typing input handler
  function handleInput(e) {
    if (isInputDisabled) return;
    if (e.type === "paste") {
      e.preventDefault();
      return;
    }
    const now = Date.now();
    const val = e.target.value;
    // error if not prefix
    if (!sentence.startsWith(val)) {
      setHasError(true);
      playError();
      return;
    }
    setHasError(false);

    const rawProg = Math.min(1, val.length / sentence.length);
    const deltaProg = rawProg - lastProg;
    const deltaTime = (now - lastProgTime) / 1000;
    const chars = deltaProg * sentence.length;
    const cps = deltaTime > 0 ? chars / deltaTime : 0;
    if (cps > MAX_CPS) return;

    setLastProg(rawProg);
    setLastProgTime(now);
    setInputValue(val);
    setPlayerProgress(rawProg);

    socket.emit("progress", {
      matchId: currentMatch.id,
      wallet: walletAddressRef.current,
      progress: rawProg,
    });

    // on fully typed
    if (val === sentence) {
      fetch(`${API}/wagers/${currentMatch.id}/complete`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ winner: walletAddressRef.current }),
      });
      setMatchStatus("finished");
      setIsInputDisabled(true);
      setRaceResult({ result: "win" });
      stopEngine();
    }
  }
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // Matchmaking buttons
  async function createMatch() {
    if (!connected) return wallet.connect();      // auto‚Äêopen wallet
    try {
      const lamports = Math.floor(wager * LAMPORTS_PER_SOL);
      const nonce    = Math.floor(Math.random() * 1_000_000);
      const { ixInit, ixDeposit, escrowPda } =
        await buildInitializeAndDepositIxns(lamports, publicKey, nonce);

      // send on‚Äêchain
      const tx = new Transaction().add(ixInit, ixDeposit);
      const sig = await wallet.sendTransaction(tx, connection);
      // no await confirm: assume success
      setCurrentMatch({ id: escrowPda.toBase58(), amount: wager, creator: walletAddressRef.current, nonce });
      socket.emit("joinMatch", escrowPda.toBase58());
      setMatchStatus("waiting");
    } catch (e) {
      console.error("Create error:", e);
      alert("Create match failed:\n" + e.message);
    }
  }

  async function acceptMatch(m) {
    if (!connected) return wallet.connect();
    setIsAccepting(true);
    try {
      const ix = await buildJoinAndDepositIxn(new PublicKey(m.id), m.amount);
      await wallet.sendTransaction(new Transaction().add(ix), connection);

      setMatchStatus("ready");
      setSentence(chooseSentence(m.id));
      setCountdown(3);

      await fetch(`${API}/wagers/${m.id}/accept`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ accepter: walletAddressRef.current }),
      });
      setCurrentMatch({ ...m, accepter: walletAddressRef.current });
      socket.emit("joinMatch", m.id);
    } catch (e) {
      console.error("Accept error:", e);
      alert("Accept error:\n" + e.message);
      setCurrentMatch(null);
    } finally {
      setIsAccepting(false);
    }
  }

  async function cancelMatch(m) {
    try {
      const ix = await buildCancelIxn(
        new PublicKey(m.id),
        new PublicKey(m.creator),
        new PublicKey(m.accepter || m.creator)
      );
      await wallet.sendTransaction(new Transaction().add(ix), connection);
      await fetch(`${API}/wagers/${m.id}/cancel`, { method: "POST" });
      resetGame();
    } catch (e) {
      console.error("Cancel error:", e);
      alert("Cancel error:\n" + e.message);
    }
  }
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  // UI renderers
  const renderMatchmaking = () => (
    <div className="matches">
      <div className="match-create">
        <h2>Create Match</h2>
        <label>Wager:</label>
        <input
          type="number" min="0.01" step="0.01"
          value={wager}
          onChange={(e) => setWager(Number(e.target.value))}
        />
        <button onClick={createMatch}>Play!</button>
      </div>
      <div className="match-list">
        <h2>Open Matches</h2>
        {openMatches.length === 0
          ? <p>No open matches</p>
          : openMatches.map(m => (
              <div key={m.id} className="match-item">
                <span>{m.id.slice(0,6)}‚Ä¶ ‚Äî {m.amount} SOL</span>
                {m.creator === walletAddressRef.current
                  ? <button onClick={()=>cancelMatch(m)}>Cancel</button>
                  : <button disabled={!!currentMatch||isAccepting}
                      onClick={()=>acceptMatch(m)}>
                      {isAccepting ? "‚Ä¶" : "Join"}
                    </button>
                }
              </div>
            ))
        }
      </div>
    </div>
  );

  const renderWaiting = () => (
    <div className="waiting">
      <h2>Waiting for opponent‚Ä¶</h2>
      <button onClick={()=>cancelMatch(currentMatch)}>Cancel</button>
    </div>
  );

  const renderRace = () => (
    <div className="race-container">
      {matchStatus === "ready" && (
        <div className="countdown">{countdown}</div>
      )}
      { (matchStatus==="racing" || matchStatus==="finished") && (
        <div className="track">
          <img src="/car-player.png" className="car player" style={{ left:`${playerProgress*100}%` }}/>
          <img src="/car-opponent.png" className="car opponent" style={{ left:`${opponentProgress*100}%` }}/>
        </div>
      )}
      <div className="type-box">
        <div className="sentence">{sentence}</div>
        <input
          ref={inputRef}
          value={inputValue}
          onChange={handleInput}
          onPaste={handleInput}
          disabled={isInputDisabled}
          style={{
            backgroundColor: hasError ? "rgba(255,0,0,0.2)" : undefined
          }}
        />
      </div>
      {matchStatus==="finished" && raceResult?.result==="win" && (
        <div className="result result-win">
          üèÜ You won!{" "}
          <button onClick={async()=>{
            try {
              await fetch(`${API}/wagers/${currentMatch.id}/resolve`,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({winner:walletAddressRef.current})});
              resetGame();
            }catch(e){console.error(e);alert(e.message);}
          }}>Claim</button>
        </div>
      )}
      {matchStatus==="finished" && raceResult?.result==="lose" && (
        <div className="result result-lose">
          üòî You lost.
          <button onClick={resetGame}>Back to Matches</button>
        </div>
      )}
    </div>
  );
  // ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ

  return (
    <div className="app">
      <header>
        <h1>Type Racer</h1>
        <div className="wallet-info">
          {connected
            ? `${walletAddressRef.current.slice(0,6)}‚Ä¶ (${walletBalance.toFixed(2)} SOL)`
            : <WalletMultiButton />
          }
        </div>
        <button className="mute-btn" onClick={()=>setMuted(m=>!m)}>
          {muted ? "üîá" : "üîä"}
        </button>
      </header>

      {!connected && <p className="prompt">Please connect to play</p>}
      {connected && !currentMatch  && renderMatchmaking()}
      {connected && currentMatch && matchStatus==="waiting" && renderWaiting()}
      {connected && currentMatch && (matchStatus==="ready" || matchStatus==="racing" || matchStatus==="finished") && renderRace()}
    </div>
  );
}
