// src/solana.js
import * as anchor from "@coral-xyz/anchor";
import { Connection, PublicKey } from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";

const RPC_URL = process.env.REACT_APP_SOLANA_RPC;
const connection = new Connection(RPC_URL, "confirmed");

// Export the program ID so you can use it in your app
const PROGRAM_ID = new PublicKey(idl.metadata.address);

// Export connection and anchor (provider & program are created dynamically in your React components)
export { connection, PROGRAM_ID, anchor };
