// src/escrow.js

import * as anchor from "@coral-xyz/anchor";
import { PublicKey, SystemProgram, Transaction } from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";
const PROGRAM_ID = new PublicKey(idl.metadata.address);

/**
 * Initialize a new escrow (first wallet confirm)
 * @param {number} wagerLamports
 * @param {anchor.AnchorProvider} provider
 * @returns {Promise<PublicKey>} PDA of escrow
 */
export async function initializeEscrow(wagerLamports, provider) {
  const program = new anchor.Program(idl, PROGRAM_ID, provider);

  // derive a unique PDA
  const nonce = Math.floor(Math.random() * 1000000);
  const [escrowPDA] = await PublicKey.findProgramAddress([Buffer.from("escrow"), provider.wallet.publicKey.toBuffer(), new anchor.BN(nonce).toArrayLike(Buffer, "le", 8)], program.programId);

  // build the initialize instruction
  const ix = await program.methods.initialize(new anchor.BN(wagerLamports), new anchor.BN(nonce)).accounts({
    escrow: escrowPDA,
    player1: provider.wallet.publicKey,
    authority: provider.wallet.publicKey,
    systemProgram: SystemProgram.programId
  }).instruction();

  // create a Transaction for the wallet adapter to sign & send
  const tx = new Transaction().add(ix);

  // sendTransaction will pop up the wallet approval dialog
  const sig = await provider.wallet.sendTransaction(tx, provider.connection);
  // wait for confirmation
  await provider.connection.confirmTransaction(sig, "confirmed");
  return escrowPDA;
}

/**
 * Deposit into an existing escrow (second wallet confirm)
 * @param {PublicKey} escrowPDA
 * @param {anchor.AnchorProvider} provider
 */
export async function depositToEscrow(escrowPDA, provider) {
  const program = new anchor.Program(idl, PROGRAM_ID, provider);
  // fetch escrow account to get its vault address
  const escrowAccount = await program.account.escrow.fetch(escrowPDA);
  const ix = await program.methods.deposit().accounts({
    escrow: escrowPDA,
    payer: provider.wallet.publicKey,
    escrowAccount: escrowAccount.escrowAccount,
    systemProgram: SystemProgram.programId
  }).instruction();
  const tx = new Transaction().add(ix);
  const sig = await provider.wallet.sendTransaction(tx, provider.connection);
  await provider.connection.confirmTransaction(sig, "confirmed");
  return sig;
}

/**
 * Player 2 joins and deposits in one go (wallet confirm)
 * @param {PublicKey} escrowPDA
 * @param {anchor.AnchorProvider} provider
 */
export async function joinAndDeposit(escrowPDA, provider) {
  const program = new anchor.Program(idl, PROGRAM_ID, provider);
  const escrowAccount = await program.account.escrow.fetch(escrowPDA);
  const ix = await program.methods.joinAndDeposit().accounts({
    escrow: escrowPDA,
    player2: provider.wallet.publicKey,
    escrowAccount: escrowAccount.escrowAccount,
    systemProgram: SystemProgram.programId
  }).instruction();
  const tx = new Transaction().add(ix);
  const sig = await provider.wallet.sendTransaction(tx, provider.connection);
  await provider.connection.confirmTransaction(sig, "confirmed");
  return sig;
}

/**
 * Cancel an escrow and refund both players (wallet confirm)
 * @param {PublicKey} escrowPDA
 * @param {PublicKey} player1Pubkey
 * @param {PublicKey} player2Pubkey
 * @param {anchor.AnchorProvider} provider
 */
export async function cancelEscrow(escrowPDA, player1Pubkey, player2Pubkey, provider) {
  const program = new anchor.Program(idl, PROGRAM_ID, provider);
  const escrowAccount = await program.account.escrow.fetch(escrowPDA);
  const ix = await program.methods.cancel().accounts({
    escrow: escrowPDA,
    player1: player1Pubkey,
    player2: player2Pubkey,
    escrowAccount: escrowAccount.escrowAccount,
    authority: provider.wallet.publicKey
  }).instruction();
  const tx = new Transaction().add(ix);
  const sig = await provider.wallet.sendTransaction(tx, provider.connection);
  await provider.connection.confirmTransaction(sig, "confirmed");
  return sig;
}

/**
 * Resolve escrow by sending all funds to the winner (wallet confirm)
 * @param {PublicKey} escrowPDA
 * @param {PublicKey} winnerPubkey
 * @param {anchor.AnchorProvider} provider
 */
export async function resolveEscrow(escrowPDA, winnerPubkey, provider) {
  const program = new anchor.Program(idl, PROGRAM_ID, provider);
  const escrowAccount = await program.account.escrow.fetch(escrowPDA);
  const ix = await program.methods.resolve(winnerPubkey).accounts({
    escrow: escrowPDA,
    winner: winnerPubkey,
    house: provider.wallet.publicKey,
    escrowAccount: escrowAccount.escrowAccount,
    authority: provider.wallet.publicKey
  }).instruction();
  const tx = new Transaction().add(ix);
  const sig = await provider.wallet.sendTransaction(tx, provider.connection);
  await provider.connection.confirmTransaction(sig, "confirmed");
  return sig;
}

