// src/solana.js

import { Connection, PublicKey } from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";

// ── RPC endpoint ───────────────────────────────────────────
const RPC_URL = process.env.REACT_APP_SOLANA_RPC;
if (!RPC_URL) {
  throw new Error("Missing REACT_APP_SOLANA_RPC in your .env");
}
export const connection = new Connection(RPC_URL, "confirmed");

// ── Program ID ────────────────────────────────────────────
// anchor-generated IDL puts the on‑chain address at the top level
if (!idl.address) {
  throw new Error("IDL is missing top‑level `address` field");
}
export const PROGRAM_ID = new PublicKey(idl.address);
