import * as anchor from "@coral-xyz/anchor";
import { getProgram } from "./solana";

/**
 * Initialize the escrow on-chain and return its PDA.
 * Only waits for the transaction to be sent, NOT confirmed!
 */
export async function initializeEscrow(wagerLamports) {
  const program = getProgram();
  const walletPk = program.provider.wallet.publicKey;

  // Random nonce for PDA uniqueness
  const nonce = new anchor.BN(Math.floor(Math.random() * Number.MAX_SAFE_INTEGER), 10, "le");
  const [escrowPda] = await anchor.web3.PublicKey.findProgramAddress([Buffer.from("escrow"), walletPk.toBuffer(), nonce.toArrayLike(Buffer, "le", 8)], program.programId);
  try {
    await program.methods.initialize(new anchor.BN(wagerLamports), nonce).accounts({
      escrow: escrowPda,
      player1: walletPk,
      authority: walletPk,
      systemProgram: anchor.web3.SystemProgram.programId
    }).rpc({
      skipPreflight: true,
      preflightCommitment: "confirmed"
    });
    // No confirmation wait here
  } catch (e) {
    console.error("initializeEscrow failed:", e);
    throw e;
  }
  return escrowPda;
}

