import { Connection, PublicKey } from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";

// Export just PROGRAM_ID and connection for frontend
export const PROGRAM_ID = new PublicKey(idl.address || idl.metadata.address);
export const connection = new Connection(process.env.REACT_APP_SOLANA_RPC, "confirmed");
