import * as anchor from "@coral-xyz/anchor";
import { Connection, PublicKey, clusterApiUrl } from "@solana/web3.js";
import { AnchorProvider, Program } from "@coral-xyz/anchor";
import idl from "./idl/typeracer_escrow.json";

let provider;
let program;

/**
 * Setup and return Anchor provider and program instance.
 * Should be called after wallet connection.
 */
export function setProvider(wallet) {
  if (!wallet) throw new Error("Wallet not connected");

  const network = process.env.REACT_APP_SOLANA_NETWORK || "mainnet-beta";
  const rpcEndpoint = process.env.REACT_APP_SOLANA_RPC || clusterApiUrl(network);

  const connection = new Connection(rpcEndpoint, "confirmed");
  provider = new AnchorProvider(connection, wallet, {
    preflightCommitment: "confirmed",
    commitment: "confirmed",
  });

  program = new Program(idl, new PublicKey(idl.address), provider);
  return program;
}

/**
 * Returns the current Anchor program instance.
 * Throws if provider/program not initialized.
 */
export function getProgram() {
  if (!program) {
    throw new Error(
      "Program not initialized. Call setProvider(wallet) first."
    );
  }
  return program;
}

/**
 * Returns current Anchor provider instance.
 */
export function getProvider() {
  if (!provider) {
    throw new Error(
      "Provider not initialized. Call setProvider(wallet) first."
    );
  }
  return provider;
}
