// frontend/src/escrow.js

import * as anchor from "@coral-xyz/anchor";
import { PublicKey, SystemProgram, TransactionInstruction } from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";
import { PROGRAM_ID, getProvider } from "./solana";

/** Lazily grab your Anchor Program; must call setProvider(wallet) first. */
function getProgram() {
  const provider = getProvider();
  return new anchor.Program(idl, PROGRAM_ID, provider);
}

/**
 * 1) Initialize & deposit
 */
export async function buildInitializeAndDepositIxns(lamports, initializerPubkey, nonce) {
  const program = getProgram();
  const nonceBuf = Buffer.from(new anchor.BN(nonce).toArray("le", 8));
  const [escrowPda] = await PublicKey.findProgramAddress([Buffer.from("escrow"), initializerPubkey.toBuffer(), nonceBuf], PROGRAM_ID);

  // initialize
  const ixInit = await program.methods.initialize(new anchor.BN(lamports), new anchor.BN(nonce)).accounts({
    escrow: escrowPda,
    player1: initializerPubkey,
    authority: initializerPubkey,
    system_program: SystemProgram.programId
  }).instruction();

  // deposit SOL
  const ixDeposit = SystemProgram.transfer({
    fromPubkey: initializerPubkey,
    toPubkey: escrowPda,
    lamports
  });
  return {
    ixInit,
    ixDeposit,
    escrowPda
  };
}

/**
 * 2) Join & deposit
 */
export async function buildJoinAndDepositIxn(escrowId) {
  const program = getProgram();
  const provider = getProvider();
  const escrowPda = new PublicKey(escrowId);
  return program.methods.joinAndDeposit().accounts({
    escrow: escrowPda,
    player2: provider.wallet.publicKey,
    escrow_account: escrowPda,
    system_program: SystemProgram.programId
  }).instruction();
}

/**
 * 3) Cancel
 */
export async function buildCancelIxn(escrowId, player1Pubkey, player2Pubkey) {
  const program = getProgram();
  const escrowPda = new PublicKey(escrowId);
  const p1 = new PublicKey(player1Pubkey);
  const p2 = new PublicKey(player2Pubkey);
  const disc = idl.instructions.find(ix => ix.name === "cancel").discriminator;
  const data = Buffer.from(disc);
  const keys = [{
    pubkey: escrowPda,
    isSigner: false,
    isWritable: true
  },
  // escrow
  {
    pubkey: p1,
    isSigner: false,
    isWritable: true
  },
  // player1
  {
    pubkey: p2,
    isSigner: false,
    isWritable: true
  },
  // player2
  {
    pubkey: escrowPda,
    isSigner: false,
    isWritable: true
  } // escrow_account
  ];
  return new TransactionInstruction({
    programId: PROGRAM_ID,
    keys,
    data
  });
}

/**
 * 4) Resolve/payout
 */
export async function buildResolveIxn(escrowId, winnerPubkey) {
  const program = getProgram();
  const escrowPda = new PublicKey(escrowId);
  const winner = new PublicKey(winnerPubkey);
  const disc = idl.instructions.find(ix => ix.name === "resolve").discriminator;
  const data = Buffer.concat([Buffer.from(disc), Buffer.from(winner.toBytes())]);
  const keys = [{
    pubkey: escrowPda,
    isSigner: false,
    isWritable: true
  },
