// src/App.jsx

import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import sentences from "./sentences";
import { useConnection, useWallet } from "@solana/wallet-adapter-react";
import { WalletMultiButton } from "@solana/wallet-adapter-react-ui";
import {
  LAMPORTS_PER_SOL,
  PublicKey,
  Transaction,
} from "@solana/web3.js";
import socket from "./socket";
import * as anchor from "@coral-xyz/anchor";
import { setProvider } from "./solana";
import {
  buildInitializeAndDepositIxns,
  buildJoinAndDepositIxn,
  buildCancelIxn,
  buildResolveIxn,
} from "./escrow";

export default function App() {
  const { connection } = useConnection();
  const wallet = useWallet();
  const { publicKey, connected } = wallet;
  const walletAddress = publicKey?.toBase58() || "";

  // ‚îÄ‚îÄ Setup Anchor provider ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  useEffect(() => {
    if (connected && connection) {
      const prov = new anchor.AnchorProvider(connection, wallet, {
        preflightCommitment: "confirmed",
        commitment: "confirmed",
      });
      anchor.setProvider(prov);
      setProvider(wallet);
    }
  }, [connected, connection, wallet]);

  // ‚îÄ‚îÄ Wallet balance (errors silenced) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  const [walletBalance, setWalletBalance] = useState(0);
  useEffect(() => {
    if (connection && publicKey) {
      connection
        .getBalance(publicKey)
        .then((lamports) => setWalletBalance(lamports / LAMPORTS_PER_SOL))
        .catch(() => {});
    }
  }, [connection, publicKey]);

  // ‚îÄ‚îÄ Game & Race State ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  const [wager, setWager] = useState(1);
  const [openMatches, setOpenMatches] = useState([]);
  const [currentMatch, setCurrentMatch] = useState(null);
  const [matchStatus, setMatchStatus] = useState("waiting");

  const [sentence, setSentence] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [playerProgress, setPlayerProgress] = useState(0);
  const [opponentProgress, setOpponentProgress] = useState(0);
  const [countdown, setCountdown] = useState(3);
  const [raceResult, setRaceResult] = useState(null);
  const [isInputDisabled, setIsInputDisabled] = useState(false);
  const [isAccepting, setIsAccepting] = useState(false);
  const [opponentWallet, setOpponentWallet] = useState(null);

  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState("");
  const inputRef = useRef(null);
  const API = process.env.REACT_APP_API_URL;

  const [lastProgTime, setLastProgTime] = useState(Date.now());
  const [lastProg, setLastProg] = useState(0);
  const MAX_CPS = 20;

  // ‚îÄ‚îÄ Socket Listeners ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  useEffect(() => {
    socket.connect();
    socket.on("openMatches", setOpenMatches);
    socket.on("newMatch", (m) => setOpenMatches((os) => [...os, m]));
    socket.on("removeMatch", (id) =>
      setOpenMatches((os) => os.filter((m) => m.id !== id))
    );
    socket.on("startMatch", ({ id, players }) => {
      setCurrentMatch({
        id,
        amount: currentMatch?.amount || 0,
        creator: players[0],
        accepter: players[1],
      });
      setMatchStatus("ready");
      setSentence(chooseSentence(id));
      setCountdown(3);
      setOpponentWallet(players.find((w) => w !== walletAddress));
    });
    socket.on("opponentProgress", ({ wallet: w, progress }) => {
      if (w !== walletAddress) setOpponentProgress(progress);
    });
    socket.on("raceEnd", ({ winner, progressMap }) => {
      setMatchStatus("finished");
      setIsInputDisabled(true);
      setRaceResult({ result: winner === walletAddress ? "win" : "lose" });
      if (progressMap) {
        setPlayerProgress(progressMap[walletAddress] || 0);
        setOpponentProgress(progressMap[opponentWallet] || 0);
      }
    });
    socket.on("chat", setChatMessages);
    socket.on("opponentLeft", () => resetGame());
    fetchOpenMatches();
    return () => {
      socket.disconnect();
      socket.removeAllListeners();
    };
  }, [walletAddress, currentMatch, opponentWallet]);

  // ‚îÄ‚îÄ Countdown Effect ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  useEffect(() => {
    if (matchStatus === "ready" && countdown > 0) {
      const t = setTimeout(() => setCountdown((c) => c - 1), 1000);
      return () => clearTimeout(t);
    } else if (matchStatus === "ready" && countdown === 0) {
      setMatchStatus("racing");
      setIsInputDisabled(false);
      inputRef.current?.focus();
    }
  }, [countdown, matchStatus]);

  // ‚îÄ‚îÄ Helpers ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  const chooseSentence = (id) =>
    sentences[
      Array.from(id).reduce((a, c) => a + c.charCodeAt(0), 0) % sentences.length
    ];

  const fetchOpenMatches = async () => {
    try {
      const res = await fetch(`${API}/wagers`);
      setOpenMatches(await res.json());
    } catch {}
  };

  const resetGame = () => {
    setCurrentMatch(null);
    setMatchStatus("waiting");
    setSentence("");
    setInputValue("");
    setPlayerProgress(0);
    setOpponentProgress(0);
    setCountdown(3);
    setRaceResult(null);
    setIsInputDisabled(false);
    setOpponentWallet(null);
    setLastProg(0);
    setLastProgTime(Date.now());
    fetchOpenMatches();
  };

  // ‚îÄ‚îÄ 1) CREATE MATCH ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  async function createMatch() {
    if (!connected) return alert("Connect wallet");
    try {
      const lamports = Math.floor(wager * LAMPORTS_PER_SOL);
      const nonce = Math.floor(Math.random() * 1e6);
      const { ixInit, ixDeposit, escrowPda } =
        await buildInitializeAndDepositIxns(lamports, publicKey, nonce);

      // fire and forget‚Äîdon‚Äôt await confirmation
      const tx = new Transaction().add(ixInit, ixDeposit);
      wallet.sendTransaction(tx, connection).catch(() => {});

      // immediately show waiting
      const id = escrowPda.toBase58();
      await fetch(`${API}/wagers`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          id,
          amount: Number(wager),
          creator: walletAddress,
          feePercent: 5,
          vault: id,
          nonce,
        }),
      });
      setCurrentMatch({ id, amount: Number(wager), creator: walletAddress, nonce });
      socket.emit("joinMatch", id);
      setMatchStatus("waiting");
    } catch (e) {
      console.error("Create match failed:", e);
      alert("Create match failed");
    }
  }

  // ‚îÄ‚îÄ 2) ACCEPT MATCH ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  async function acceptMatch(m) {
    if (!connected) return alert("Connect wallet");
    setIsAccepting(true);
    try {
      const ix = await buildJoinAndDepositIxn(new PublicKey(m.id));
      const tx = new Transaction().add(ix);
      wallet.sendTransaction(tx, connection).catch(() => {});

      // immediately start racing locally
      setCurrentMatch(m);
      setMatchStatus("racing");
      setSentence(chooseSentence(m.id));
      setInputValue("");
      setPlayerProgress(0);
      setOpponentProgress(0);
      setIsInputDisabled(false);

      await fetch(`${API}/wagers/${m.id}/accept`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ accepter: walletAddress, vault: m.id }),
      });
    } catch (e) {
      console.error("Accept failed:", e);
      alert("Accept failed");
    } finally {
      setIsAccepting(false);
    }
  }

  // ‚îÄ‚îÄ 3) CANCEL MATCH ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  async function cancelMatch(m) {
    try {
      const escrowPda = new PublicKey(m.id);
      const ix = buildCancelIxn(
        escrowPda,
        publicKey,
        new PublicKey(m.accepter || m.creator)
      );
      const tx = new Transaction().add(ix);
      wallet.sendTransaction(tx, connection).catch(() => {});
      await fetch(`${API}/wagers/${m.id}/cancel`, { method: "POST" });
      resetGame();
    } catch (e) {
      console.error("Cancel failed:", e);
      alert("Cancel failed");
    }
  }

  // ‚îÄ‚îÄ 4) TYPING INPUT ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  function handleInput(e) {
    if (isInputDisabled) return;
    if (e.type === "paste") {
      e.preventDefault();
      return;
    }
    const now = Date.now();
    const val = e.target.value;
    const prog = Math.min(1, val.length / sentence.length);
    const delta = prog - lastProg;
    const dt = (now - lastProgTime) / 1000;
    const chars = delta * sentence.length;
    const cps = dt > 0 ? chars / dt : 0;
    if (cps > MAX_CPS) return;
    setLastProg(prog);
    setLastProgTime(now);
    setInputValue(val);
    setPlayerProgress(prog);
    socket.emit("progress", {
      matchId: currentMatch.id,
      wallet: walletAddress,
      progress: prog,
    });
    if (val === sentence) {
      fetch(`${API}/wagers/${currentMatch.id}/complete`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ winner: walletAddress }),
      });
      setMatchStatus("finished");
      setIsInputDisabled(true);
      setRaceResult({ result: "win" });
    }
  }

  // ‚îÄ‚îÄ RENDER ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
  return (
    <div className="app">
      <header>
        <h1>Type Racer</h1>
        {connected ? (
          <div className="wallet-info">
            {walletAddress.slice(0, 6)}‚Ä¶ ({walletBalance.toFixed(2)} SOL)
          </div>
        ) : (
          <WalletMultiButton />
        )}
      </header>
      {!connected && <p>Please connect your wallet to play.</p>}

      {connected && !currentMatch && (
        <section className="matchmaking">
          <h2>Create a Match</h2>
          <label>
            Wager (SOL):
            <input
              type="number"
              min="0.01"
              step="0.01"
              value={wager}
              onChange={(e) => setWager(Number(e.target.value))}
            />
          </label>
          <button onClick={createMatch} disabled={!wager || wager <= 0}>
            Create Match
          </button>
          <h2>Open Matches</h2>
          <ul>
            {openMatches.map((m) => (
              <li key={m.id}>
                {m.id.slice(0, 6)}‚Ä¶ | {m.amount} SOL{" "}
                {m.creator === walletAddress ? (
                  <button onClick={() => cancelMatch(m)}>Cancel</button>
                ) : (
                  <button
                    onClick={() => acceptMatch(m)}
                    disabled={!!currentMatch || isAccepting}
                  >
                    {isAccepting ? "Accepting‚Ä¶" : "Accept"}
                  </button>
                )}
              </li>
            ))}
          </ul>
        </section>
      )}

      {connected && currentMatch && matchStatus === "racing" && (
        <section className="race-section">
          <div className="sentence-display">{sentence}</div>
          <input
            ref={inputRef}
            type="text"
            value={inputValue}
            onChange={handleInput}
            onPaste={handleInput}
            placeholder="Start typing‚Ä¶"
            disabled={isInputDisabled}
            autoFocus
          />
          <div className="track">
            <div className="lane">
              <div
                className="car car-player"
                style={{ left: `${playerProgress * 100}%` }}
              >
                üöó
              </div>
            </div>
            <div className="lane">
              <div
                className="car car-opponent"
                style={{ left: `${opponentProgress * 100}%` }}
              >
                üèéÔ∏è
              </div>
            </div>
          </div>
        </section>
      )}

      {connected && currentMatch && matchStatus === "finished" && (
        <section className="results-chat">
          {raceResult?.result === "win" ? (
            <div className="results results-win">
              <h2>üèÜ You Won!</h2>
              <button
                onClick={async () => {
                  const ix = await buildResolveIxn(
                    new PublicKey(currentMatch.id),
                    publicKey
                  );
                  const tx = new Transaction().add(ix);
                  wallet.sendTransaction(tx, connection).catch(() => {});
                  resetGame();
                }}
              >
                Claim Winnings
              </button>
            </div>
          ) : (
            <div className="results results-lose">
              <h2>üò¢ You Lost!</h2>
              <button onClick={resetGame}>Back to Matches</button>
            </div>
          )}
          <div className="chat-section">
            <h2>Chat</h2>
            <div className="chat-messages">
              {chatMessages.map((msg, i) => (
                <p key={i}>
                  <strong>{msg.sender.slice(0, 6)}‚Ä¶:</strong> {msg.message}
                </p>
              ))}
            </div>
            <input
              value={chatInput}
              onChange={(e) => setChatInput(e.target.value)}
              onKeyDown={(e) =>
                e.key === "Enter" &&
                socket.emit("message", {
                  matchId: currentMatch.id,
                  sender: walletAddress,
                  message: chatInput,
                }) &&
                setChatInput("")
              }
            />
            <button
              onClick={() => {
                socket.emit("message", {
                  matchId: currentMatch.id,
                  sender: walletAddress,
                  message: chatInput,
                });
                setChatInput("");
              }}
            >
              Send
            </button>
          </div>
        </section>
      )}
    </div>
  );
}
