// frontend/src/escrow.js

import * as anchor from "@coral-xyz/anchor";
import { PublicKey, SystemProgram } from "@solana/web3.js";
import idl from "./idl/typeracer_escrow.json";
import { PROGRAM_ID, getProvider } from "./solana";

/** Lazily grab the Anchor Program (throws if setProvider wasn’t called) */
function getProgram() {
  const provider = getProvider();
  if (!provider) {
    throw new Error(
      "Provider not set: call setProvider(wallet) in App.jsx before using escrow.js"
    );
  }
  return new anchor.Program(idl, PROGRAM_ID, provider);
}

/**
 * 1) Initialize + deposit
 * @param lamports      amount of SOL in lamports
 * @param initializerPubkey
 * @param nonce         random number
 */
export async function buildInitializeAndDepositIxns(
  lamports,
  initializerPubkey,
  nonce
) {
  const program = getProgram();

  // pack the nonce as 8‑byte LE
  const nonceBuf = Buffer.from(new anchor.BN(nonce).toArray("le", 8));

  // derive PDA as in Rust: ["escrow", player1, &nonce.to_le_bytes()]
  const [escrowPda] = await PublicKey.findProgramAddress(
    [Buffer.from("escrow"), initializerPubkey.toBuffer(), nonceBuf],
    PROGRAM_ID
  );

  // 1️⃣ initialize(wager, nonce)
  const ixInit = await program.methods
    .initialize(new anchor.BN(lamports), new anchor.BN(nonce))
    .accounts({
      escrow:         escrowPda,                      // PDA account
      player1:        initializerPubkey,               // signer
      authority:      initializerPubkey,               // signer = payer
      system_program: SystemProgram.programId,         // <— snake_case
    })
    .instruction();

  // 2️⃣ deposit SOL
  const ixDeposit = SystemProgram.transfer({
    fromPubkey: initializerPubkey,
    toPubkey: escrowPda,
    lamports,
  });

  return { ixInit, ixDeposit, escrowPda };
}

/**
 * 2) Join & deposit in one go
 * @param escrowPda
 * @param wager
 */
export async function buildJoinAndDepositIxn(escrowPda, wager) {
  const program = getProgram();

  return program.methods
    .joinAndDeposit(new anchor.BN(wager))
    .accounts({
      escrow:         escrowPda,                          // Account<Escrow>
      player2:        program.provider.wallet.publicKey,  // signer
      escrowAccount: escrowPda,                          // raw PDA for lamports
      system_program: SystemProgram.programId,            // <— snake_case
    })
    .instruction();
}

/**
 * 3) Cancel an escrow
 * @param escrowPda
 * @param initializerPubkey
 * @param accepterPubkey
 */
export async function buildCancelIxn(
  escrowPda,
  initializerPubkey,
  accepterPubkey
) {
  const program = getProgram();

  return program.methods
    .cancel()
    .accounts({
      escrow:         escrowPda,            // #[account(mut)]
      player1:        initializerPubkey,     // #[account(mut)]
      player2:        accepterPubkey,        // #[account(mut)]
      escrowAccount: escrowPda,             // #[account(mut, address = escrow.key())]
      authority:      initializerPubkey,     // #[account(mut)] signer
    })
    .instruction();
}

/**
 * 4) Resolve/payout
 * @param escrowPda
 * @param winnerPubkey
 */
export async function buildResolveIxn(escrowPda, winnerPubkey) {
  const program = getProgram();
  const winnerKey =
    typeof winnerPubkey === "string"
      ? new PublicKey(winnerPubkey)
      : winnerPubkey;

  return program.methods
    .resolve(winnerKey)
    .accounts({
      escrow:         escrowPda,                          // #[account(mut)]
      winner:         winnerKey,                          // #[account(mut)]
      house:          program.provider.wallet.publicKey,  // signer
      escrowAccount: escrowPda,                          // #[account(mut)]
    })
    .instruction();
}
