// src/App.jsx

import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import sentences from "./sentences";
import { useConnection, useWallet } from "@solana/wallet-adapter-react";
import { WalletMultiButton } from "@solana/wallet-adapter-react-ui";
import {
  LAMPORTS_PER_SOL,
  PublicKey,
  Transaction,
} from "@solana/web3.js";
import socket from "./socket";
import * as anchor from "@coral-xyz/anchor";
import { setProvider } from "./solana";
import {
  buildInitializeAndDepositIxns,
  buildJoinAndDepositIxn,
  buildCancelIxn,
} from "./escrow";

export default function App() {
  const { connection } = useConnection();
  const wallet = useWallet();
  const { publicKey, connected } = wallet;
  const walletAddress = publicKey?.toBase58() || "";

  // keep a ref to the latest wallet address for socket callbacks
  const walletAddressRef = useRef(walletAddress);
  useEffect(() => {
    walletAddressRef.current = walletAddress;
  }, [walletAddress]);

  // set up Anchor + provider
  useEffect(() => {
    if (connected && connection) {
      const prov = new anchor.AnchorProvider(connection, wallet, {
        preflightCommitment: "confirmed",
        commitment: "confirmed",
      });
      anchor.setProvider(prov);
      setProvider(wallet);
    }
  }, [connected, connection, wallet]);

  // track SOL balance
  const [walletBalance, setWalletBalance] = useState(0);
  useEffect(() => {
    if (connection && publicKey) {
      connection.getBalance(publicKey).then((lamports) => {
        setWalletBalance(lamports / LAMPORTS_PER_SOL);
      });
    }
  }, [connection, publicKey]);

  // game state
  const [wager, setWager] = useState(1);
  const [openMatches, setOpenMatches] = useState([]);
  const [currentMatch, setCurrentMatch] = useState(null);
  const [matchStatus, setMatchStatus] = useState("waiting");

  // race state
  const [sentence, setSentence] = useState("");
  const [inputValue, setInputValue] = useState("");
  const [playerProgress, setPlayerProgress] = useState(0);
  const [opponentProgress, setOpponentProgress] = useState(0);
  const [countdown, setCountdown] = useState(3);
  const [raceResult, setRaceResult] = useState(null);
  const [isInputDisabled, setIsInputDisabled] = useState(false);
  const [isAccepting, setIsAccepting] = useState(false);
  const [opponentWallet, setOpponentWallet] = useState(null);

  // chat
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState("");

  const inputRef = useRef(null);
  const API = process.env.REACT_APP_API_URL;

  // anti‑cheat
  const [lastProgTime, setLastProgTime] = useState(Date.now());
  const [lastProg, setLastProg] = useState(0);
  const MAX_CPS = 20;

  // pick a consistent sentence based on match ID
  const chooseSentence = (id) =>
    sentences[
      Array.from(id).reduce((a, c) => a + c.charCodeAt(0), 0) %
        sentences.length
    ];

  const fetchOpenMatches = async () => {
    try {
      const res = await fetch(`${API}/wagers`);
      setOpenMatches(await res.json());
    } catch {}
  };

  const resetGame = () => {
    setCurrentMatch(null);
    setMatchStatus("waiting");
    setSentence("");
    setInputValue("");
    setPlayerProgress(0);
    setOpponentProgress(0);
    setCountdown(3);
    setRaceResult(null);
    setIsInputDisabled(false);
    setOpponentWallet(null);
    setLastProg(0);
    setLastProgTime(Date.now());
    fetchOpenMatches();
  };

  // ───────── SOCKET SETUP ─────────
  useEffect(() => {
    socket.connect();
    socket.on("openMatches", setOpenMatches);
    socket.on("newMatch", (m) =>
      setOpenMatches((os) => [...os, m])
    );
    socket.on("removeMatch", (id) =>
      setOpenMatches((os) => os.filter((m) => m.id !== id))
    );
    socket.on("startMatch", ({ id, players }) => {
      if (!currentMatch || currentMatch.id !== id) {
        setCurrentMatch({
          id,
          amount: currentMatch?.amount || 0,
          creator: players[0],
          accepter: players[1],
        });
      }
      setMatchStatus("ready");
      setSentence(chooseSentence(id));
      setCountdown(3);
      setOpponentWallet(
        players.find((w) => w !== walletAddressRef.current)
      );
    });
    socket.on("opponentProgress", ({ wallet: w, progress }) => {
      if (w !== walletAddressRef.current)
        setOpponentProgress(progress);
    });
    socket.on("raceEnd", ({ winner, progressMap }) => {
      setMatchStatus("finished");
      setIsInputDisabled(true);
      setRaceResult({
        result:
          winner === walletAddressRef.current
            ? "win"
            : "lose",
      });
      if (progressMap) {
        setPlayerProgress(
          progressMap[walletAddressRef.current] ?? 0
        );
        setOpponentProgress(
          progressMap[opponentWallet] ?? 0
        );
      }
    });
    socket.on("chat", setChatMessages);
    socket.on("opponentLeft", () => {
      resetGame();
    });

    // initial load
    fetchOpenMatches();

    return () => {
      socket.removeAllListeners();
      socket.disconnect();
    };
  }, []); // run once

  // countdown to race start
  useEffect(() => {
    if (matchStatus === "ready" && countdown > 0) {
      const t = setTimeout(
        () => setCountdown((c) => c - 1),
        1000
      );
      return () => clearTimeout(t);
    } else if (
      matchStatus === "ready" &&
      countdown === 0
    ) {
      setMatchStatus("racing");
      setIsInputDisabled(false);
      inputRef.current?.focus();
    }
  }, [countdown, matchStatus]);

  // ─── createMatch ─────────────────
  async function createMatch() {
    if (!connected) return alert("Connect wallet");
    const lamports = Math.floor(
      wager * LAMPORTS_PER_SOL
    );

    // only pass wagerLamports here
    const { ixInit, ixDeposit, escrowPda } =
      await buildInitializeAndDepositIxns(lamports);

    const tx = new Transaction().add(ixInit, ixDeposit);
    const sig = await wallet.sendTransaction(
      tx,
      connection
    );
    console.log("createMatch tx:", sig);

    // register with backend
    const id = escrowPda.toBase58();
    await fetch(`${API}/wagers`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        id,
        amount: Number(wager),
        creator: walletAddressRef,
        feePercent: 5,
        vault: escrowPda.toBase58(),
      }),
    });
    setCurrentMatch({
      id,
      amount: Number(wager),
      creator: walletAddressRef.current,
    });
    socket.emit("joinMatch", id);
    setMatchStatus("waiting");
  }

  // ─── acceptMatch ─────────────────
  async function acceptMatch(m) {
    if (!connected) return alert("Connect wallet");
    setIsAccepting(true);
    try {
      const ix = await buildJoinAndDepositIxn(
        new PublicKey(m.id)
      );
      await wallet.sendTransaction(
        new Transaction().add(ix),
        connection
      );

      setMatchStatus("ready");
      setSentence(chooseSentence(m.id));
      setCountdown(3);

      await fetch(
        `${API}/wagers/${m.id}/accept`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            accepter: walletAddressRef.current,
          }),
        }
      );
      setCurrentMatch({
        ...m,
        accepter: walletAddressRef.current,
      });
      socket.emit("joinMatch", m.id);
    } catch (e) {
      console.error("Accept error:", e);
      alert("Accept error:\n" + e.message);
      setCurrentMatch(null);
    } finally {
      setIsAccepting(false);
    }
  }

  // ─── cancelMatch ─────────────────
  async function cancelMatch(m) {
    try {
      const ix = await buildCancelIxn(
        new PublicKey(m.id),
        new PublicKey(m.creator),
        new PublicKey(m.accepter || m.creator)
      );
      await wallet.sendTransaction(
        new Transaction().add(ix),
        connection
      );

      await fetch(
        `${API}/wagers/${m.id}/cancel`,
        {
          method: "POST",
        }
      );
      resetGame();
    } catch (e) {
      console.error("Cancel error:", e);
      alert("Cancel error:\n" + e.message);
    }
  }

  // ─── handle typing & finish ───────
  function handleInput(e) {
    if (isInputDisabled) return;
    if (e.type === "paste") {
      e.preventDefault();
      return;
    }
    const now = Date.now();
    const val = e.target.value;
    const rawProg = Math.min(
      1,
      val.length / sentence.length
    );
    const deltaProg = rawProg - lastProg;
    const deltaTime = (now - lastProgTime) / 1000;
    const chars = deltaProg * sentence.length;
    const cps =
      deltaTime > 0 ? chars / deltaTime : 0;
    if (cps > MAX_CPS) return;

    setLastProg(rawProg);
    setLastProgTime(now);
    setInputValue(val);
    setPlayerProgress(rawProg);

    socket.emit("progress", {
      matchId: currentMatch.id,
      wallet: walletAddressRef.current,
      progress: rawProg,
    });

    if (val === sentence) {
      fetch(
        `${API}/wagers/${currentMatch.id}/complete`,
        {
          method: "POST",
          headers: {
            "Content-Type":
              "application/json",
          },
          body: JSON.stringify({
            winner:
              walletAddressRef.current,
          }),
        }
      );
      setMatchStatus("finished");
      setIsInputDisabled(true);
      setRaceResult({ result: "win" });
    }
  }

  // ─── render UIs ─────────────────
  const renderMatchmaking = () => (
    <section className="matchmaking">
      <h2>Create a Match</h2>
      <label>
        Wager (SOL):
        <input
          type="number"
          min="0.01"
          step="0.01"
          value={wager}
          onChange={(e) =>
            setWager(Number(e.target.value))
          }
        />
      </label>
      <button
        onClick={createMatch}
        disabled={!wager || wager <= 0}
      >
        Create Match
      </button>
      <h2 style={{ marginTop: 20 }}>
        Open Matches
      </h2>
      {openMatches.length === 0 ? (
        <p>No open matches. Create one!</p>
      ) : (
        <ul>
          {openMatches.map((m) => (
            <li key={m.id}>
              {m.id.slice(0, 6)}… | {m.amount} SOL{" "}
              {m.creator ===
              walletAddressRef.current ? (
                <button
                  onClick={() => cancelMatch(m)}
                >
                  Cancel
                </button>
              ) : (
                <button
                  onClick={() =>
                    acceptMatch(m)
                  }
                  disabled={
                    !!currentMatch ||
                    isAccepting
                  }
                >
                  {isAccepting
                    ? "Accepting…"
                    : "Accept"}
                </button>
              )}
            </li>
          ))}
        </ul>
      )}
    </section>
  );

  const renderWaiting = () => (
    <section className="waiting-section">
      <h2>Waiting for opponent…</h2>
      <button
        onClick={() =>
          cancelMatch(currentMatch)
        }
      >
        Cancel Match
      </button>
    </section>
  );

  const renderRace = () => (
    <section className="race-section">
      {matchStatus === "ready" && (
        <h2>Starting in {countdown}</h2>
      )}
      {matchStatus === "racing" && (
        <>
          <div className="sentence-display">
            {sentence}
          </div>
          <input
            ref={inputRef}
            type="text"
            value={inputValue}
            onChange={handleInput}
            onPaste={handleInput}
            placeholder="Start typing…"
            disabled={isInputDisabled}
          />
          <div className="track">
            <div className="lane">
              <div
                className="car car-player"
                style={{
                  left: `${
                    playerProgress * 100
                  }%`,
                }}
              >
                🚗
              </div>
            </div>
            <div className="lane">
              <div
                className="car car-opponent"
                style={{
                  left: `${
                    opponentProgress *
                    100
                  }%`,
                }}
              >
                🏎️
              </div>
            </div>
          </div>
        </>
      )}
      {matchStatus === "finished" &&
        raceResult?.result ===
          "win" && (
          <div className="results results-win">
            <h2
              style={{ fontSize: "2rem" }}
            >
              🏆 You Won!
            </h2>
            <button
              onClick={async () => {
                try {
                  const res = await fetch(
                    `${API}/wagers/${currentMatch.id}/resolve`,
                    {
                      method: "POST",
                      headers: {
                        "Content-Type":
                          "application/json",
                      },
                      body: JSON.stringify({
                        winner:
                          walletAddressRef.current,
                      }),
                    }
                  );
                  const data =
                    await res.json();
                  if (data.success) {
                    alert(
                      "Winnings claimed!\nSignature: " +
                        data.signature
                    );
                    resetGame();
                  } else {
                    throw new Error(
                      data.error ||
                        "Failed to claim winnings"
                    );
                  }
                } catch (e) {
                  console.error(
                    "Claim error:",
                    e
                  );
                  alert(
                    "Payout error:\n" +
                      e.message
                  );
                }
              }}
            >
              Claim Winnings
            </button>
          </div>
        )}
      {matchStatus === "finished" &&
        raceResult?.result ===
          "lose" && (
          <div
            className="results results-lose"
            style={{ textAlign: "center" }}
          >
            <h2
              style={{
                fontSize: "2rem",
                color: "#c0392b",
              }}
            >
              😢 You Lost!
            </h2>
            <p>Better luck next time!</p>
            <button onClick={resetGame}>
              Back to Matches
            </button>
          </div>
        )}
    </section>
  );

  const renderChat = () => (
    <section className="chat-section">
      <h2>Chat</h2>
      <div
        className="chat-messages"
        style={{
          maxHeight: 150,
          overflowY: "auto",
        }}
      >
        {chatMessages.map((msg, i) => (
          <p
            key={i}
            className={
              msg.sender ===
              walletAddressRef.current
                ? "self"
                : "other"
            }
          >
            <strong>
              {msg.sender.slice(0, 6)}…:
            </strong>{" "}
            {msg.message}
          </p>
        ))}
      </div>
      <input
        value={chatInput}
        onChange={(e) =>
          setChatInput(e.target.value)
        }
        onKeyDown={(e) => {
          if (e.key === "Enter") {
            socket.emit("message", {
              matchId: currentMatch.id,
              sender:
                walletAddressRef.current,
              message: chatInput,
            });
            setChatInput("");
          }
        }}
      />
      <button
        onClick={() => {
          socket.emit("message", {
            matchId: currentMatch.id,
            sender:
              walletAddressRef.current,
            message: chatInput,
          });
          setChatInput("");
        }}
        disabled={!chatInput.trim()}
      >
        Send
      </button>
    </section>
  );

  return (
    <div className="app">
      <header>
        <h1>Type Racer</h1>
        {connected ? (
          <div className="wallet-info">
            {walletAddressRef.current.slice(
              0,
              6
            )}
            … ({walletBalance.toFixed(2)} SOL)
          </div>
        ) : (
          <WalletMultiButton />
        )}
      </header>

      {!connected && (
        <p>Please connect your wallet to play.</p>
      )}
      {connected && !currentMatch &&
        renderMatchmaking()}
      {connected &&
        currentMatch &&
        matchStatus === "waiting" &&
        renderWaiting()}
      {connected &&
        currentMatch &&
        (matchStatus === "ready" ||
          matchStatus === "racing" ||
          matchStatus === "finished") &&
        renderRace()}
      {connected &&
        currentMatch &&
        matchStatus === "finished" &&
        renderChat()}
    </div>
  );
}
